mapper.insert:
INSERT INTO admin_operation_log (
    log_id, admin_id, operation_type,
    operation_content, create_time
) VALUES (
    #{logId}, #{adminId}, #{operationType},
    #{operationContent}, #{createTime}
)

mapper.select:
SELECT l.*, u.username as admin_name
FROM admin_operation_log l
JOIN user u ON l.admin_id = u.user_id
WHERE l.create_time BETWEEN #{startTime} AND #{endTime}
ORDER BY l.create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM admin_operation_log
WHERE create_time BETWEEN #{startTime} AND #{endTime}
