mapper.insert:
INSERT INTO user (
    user_id, username, password, id_number, 
    phone, email, user_type, status, create_time
) VALUES (
    #{userId}, #{username}, #{password}, #{idNumber}, 
    #{phone}, #{email}, #{userType}, #{status}, #{createTime}
)

mapper.update:
UPDATE user 
SET phone = #{phone},
    email = #{email}
WHERE user_id = #{userId}
